import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Employee } from '../employee.model';
import { EmployeeService } from '../employee.service';
import { HttpErrorResponse } from '@angular/common/http';
import { ActivatedRoute, Router } from '@angular/router';

@Component({
  selector: 'app-employee',
  templateUrl: './employee.component.html',
  styleUrls: ['./employee.component.css']
})
export class EmployeeComponent implements OnInit {
  employeeForm!: FormGroup; // Initialize as undefined or use '!' to assert non-null
  isCreateEmployee: boolean = true;
  employee: Employee = {} as Employee;
  skills: string[] = [];

  constructor(
    private formBuilder: FormBuilder,
    private employeeService: EmployeeService,
    private router: Router,
    private activatedRoute: ActivatedRoute
  ) {}

  ngOnInit(): void {
    this.employeeForm = this.formBuilder.group({
      emp_id: [{ value: '', disabled: true }],
      employee_name: ['', Validators.required],
      empl_contact_number: ['', Validators.required],
      employee_address: ['', Validators.required],
      employee_gender: ['', Validators.required],
      employee_depart: ['', Validators.required],
      employee_skills: ['', Validators.required],
      salary: [0, Validators.required],
      hireDate: [null, Validators.required]
    });

    this.employee = this.activatedRoute.snapshot.data['employee'];

    if (this.employee && this.employee.emp_id > 0) {
      this.isCreateEmployee = false;
      if (this.employee.employee_skills != '') {
        this.skills = this.employee.employee_skills.split(',');
      }
      // Patch form values if editing existing employee
      this.employeeForm.patchValue({
        emp_id: this.employee.emp_id,
        employee_name: this.employee.employee_name,
        empl_contact_number: this.employee.empl_contact_number,
        employee_address: this.employee.employee_address,
        employee_gender: this.employee.employee_gender,
        employee_depart: this.employee.employee_depart,
        employee_skills: this.employee.employee_skills,
        salary: this.employee.salary,
        hireDate: new Date(this.employee.hireDate) // Assuming hireDate is in ISO format
      });
    } else {
      this.isCreateEmployee = true;
    }
  }
  clearForm(): void {
    this.employeeForm.reset();
    this.employee = {} as Employee;
    this.skills = [];
  }

  saveEmployee(): void {
    // Handle form submission
    if (this.employeeForm.valid) {
      this.employee = this.employeeForm.value;
      if (this.isCreateEmployee) {
        this.employeeService.saveEmployee(this.employee).subscribe({
          next: (res: Employee) => {
            console.log(res);
            this.employeeForm.reset();
            this.skills = [];
            this.router.navigate(['/employee-list']);
          },
          error: (err: HttpErrorResponse) => {
            console.log(err);
          }
        });
      } else {
        this.employeeService.updateEmployee(this.employee).subscribe({
          next: (res: Employee) => {
            this.router.navigate(['/employee-list']);
          },
          error: (err: HttpErrorResponse) => {
            console.log(err);
          }
        });
      }
    }
  }

  selectGender(gender: string): void {
    this.employeeForm.get('employee_gender')?.setValue(gender);
  }

  onSkillsChanges(event: any): void {
    console.log(event);
    const skillsControl = this.employeeForm.get('employee_skills');
    if (skillsControl) {
      const currentValue = skillsControl.value || '';
      const skillValue = event.source.value;
      if (event.checked && !currentValue.includes(skillValue)) {
        currentValue.push(skillValue);
      } else if (!event.checked && currentValue.includes(skillValue)) {
        const index = currentValue.indexOf(skillValue);
        if (index !== -1) {
          currentValue.splice(index, 1);
        }
      }
      skillsControl.setValue(currentValue);
    }
  }
}
