<div class="container">

    <div align="right">
        <button mat-raised-button color="accent" routerLink="/employee-list">Go to Employee List</button>
    </div>

    <div class="card p-5 mt-2">
        <form [formGroup]="employeeForm" (ngSubmit)="saveEmployee()">
            <mat-form-field appearance="outline">
                <mat-label>Employee Id</mat-label>
                <input matInput placeholder="Employee Id" formControlName="emp_id" [disabled]="true">
                <mat-icon matSuffix>sentiment_very_satisfied</mat-icon>
              </mat-form-field>
            <!-- Other form fields -->

            <mat-form-field appearance="outline">
                <mat-label>Hire Date</mat-label>
                <input matInput [matDatepicker]="picker" placeholder="Hire Date" formControlName="hireDate">
                <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
                <mat-datepicker #picker></mat-datepicker>
                <mat-icon matSuffix>event</mat-icon>
            </mat-form-field>

            <!-- Other form fields -->

            <mat-divider></mat-divider>

            <div class="text-center mt-3">
                <button mat-raised-button class="m-2" type="button" (click)="clearForm()">Clear</button>
                <button mat-raised-button color="primary" class="m-2" [disabled]="employeeForm.invalid">Save</button>
            </div>
        </form>
    </div>
</div>
