import { Component, OnInit } from '@angular/core';
import { Employee } from '../employee.model';
import { EmployeeService } from '../employee.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-details',
  templateUrl: './details.component.html',
  styleUrls: ['./details.component.css']
})
export class DetailsComponent implements OnInit {

  employees: []=[];
  dataSource: Employee[] = [];

  displayedColumns: string[] = ['reviewDate', 'reviewDescription'];

  constructor(private employeeService: EmployeeService,
    private router: Router) {

    //this.getEmployeeList();
  }

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }



}
<h2>details works!</h2>


<table mat-table [dataSource]="dataSource" class="mat-elevation-z8">




    <!-- Add other columns as needed -->



    <ng-container matColumnDef="reviewDate">
      <th mat-header-cell *matHeaderCellDef> Review Date </th>
      <td mat-cell *matCellDef="let element"> {{element.reviewDate | date}} </td>
    </ng-container>

    <ng-container matColumnDef="reviewDescription">
      <th mat-header-cell *matHeaderCellDef> Review Description </th>
      <td mat-cell *matCellDef="let element"> {{element.reviewDescription}} </td>
    </ng-container>

    <!-- Add other columns as needed -->







    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
